sys_script_include.script:
var utilitiesNowProClient = Class.create();
utilitiesNowProClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getSysIdUser: function() {
        var nomeUsuario = this.getParameter('sysparm_nomeUser');
        var gr = new GlideRecord('sys_user');
        gr.addEncodedQuery("name=" + nomeUsuario);
        gr.query();

        while(gr.next()) {
            var sys_id = gr.getUniqueValue();

            return sys_id;

        }


    },

    type: 'utilitiesNowProClient'
});
