sys_ui_action.client_script_v2:
function onClick(g_form) {
    var pontofocal = [];
    pontofocal = current.ponto_focal.toString();

    var gerenteEspecialista = [];
    gerenteEspecialista = current.gerente_especialista.toString();

    var user = gs.getUserID();

    if (pontofocal.indexOf(user) !== -1 || gerenteEspecialista.indexOf(user) !== -1) {
        //gs.addErrorMessage("entrou");
        current.status_da_solicitacao = "em_andamento";
        current.aceitar_a_solicitacao = "1";
        current.comments = 'Aceite aprovado por: ' + gs.getUserName() + ' - ' + gs.getUserDisplayName();
        current.update();
        action.setRedirectURL(current);
    } else {
        gs.addErrorMessage("Meu nobre! ta tentando aprovar uma  coisa que não te pertence? pode não zé!");
    }

}

