sys_ui_action.client_script_v2:
function onClick(g_form) {

var sys_id = current.sys_id;
var user = gs.getUserID();
var gr = new GlideRecord('sysapproval_approver');
gr.addEncodedQuery('sysapproval=' + sys_id + '^state=requested^approver=' + user);
gr.query();
if (gr.next()) {
    gr.state = 'rejected';
    gr.update('Tarefa Reprovada!');

    var grApr = new GlideRecord('x_744286_informaco_aprova_es_infoti');
    grApr.get(sys_id);
    //grApr.u_state = 3;
	//grApr.aprovador = user;
	grApr.approval = 'rejected';
	grApr.comments = 'Tarefa reprovada por ' + gs.getUserDisplayName() + '.';
   // grApr.update('Tarefa salva com sucesso');
    action.openGlideRecord(grApr);
	gs.addInfoMessage("A Tarefa foi reprovada");

}

}

